package main

import (
	"context"
	"fmt"
	"github.com/coreos/etcd/clientv3"
	"time"
)

func main() {
	config := clientv3.Config{
		Endpoints:   []string{"127.0.0.1:2379"},
		DialTimeout: 5 * time.Second,
	}
	client, err := clientv3.New(config)
	if err != nil {
		fmt.Println(err)
	}
	lease := clientv3.NewLease(client)
	// Grant
	// Revoke
	// TimeToLiveTTL
	// Leasesetcd
	// KeepAlive
	leaseResp, err := lease.Grant(context.TODO(), 10) //10TTL
	if err != nil {
		fmt.Println(err)
	}
	leaseID := leaseResp.ID
	ctx, cancelFunc := context.WithCancel(context.TODO())
	// defer
	defer cancelFunc()
	defer lease.Revoke(context.TODO(), leaseID)

	// channel
	keepChan, err := lease.KeepAlive(ctx, leaseID)
	if err != nil {
		fmt.Println(err)
	}

	// 
	go func() {
		for {
			select {
			case keepResp := <-keepChan:
				if keepChan == nil {
					fmt.Println("lease out")
					goto END
				} else {
					fmt.Println("get resp", keepResp.ID)
				}
			}
		}
	END:
	}()
	kv := clientv3.NewKV(client)
	//putkv10
	putResp, err := kv.Put(context.TODO(), "/cron/lock/job1", "", clientv3.WithLease(leaseID))
	if err != nil {
		fmt.Println(err)
		return
	}

	fmt.Println(":", putResp.Header.Revision)

	//key
	for {
		getResp, err := kv.Get(context.TODO(), "/cron/lock/job1")
		if err != nil {
			fmt.Println(err)
			return
		}
		if getResp.Count == 0 {
			fmt.Println("kv")
			break
		}
		fmt.Println(":", getResp.Kvs)
		time.Sleep(time.Second)
	}
}

