package main

import (
	"context"
	"fmt"
	"go.etcd.io/etcd/clientv3"
	"time"
)

type ETCDMutex struct {
	Ttl     int64
	Conf    clientv3.Config
	Key     string
	cancel  context.CancelFunc
	lease   clientv3.Lease
	leaseID clientv3.LeaseID
	txn     clientv3.Txn
}

func (em *ETCDMutex) init() error {
	client, err := clientv3.New(em.Conf)
	if err != nil {
		return err
	}
	em.txn = clientv3.KV(client).Txn(context.TODO())

	if err != nil {
		return err
	}
	em.lease = clientv3.NewLease(client)
	leaseResp, err := em.lease.Grant(context.TODO(), em.Ttl)
	if err != nil {
		return err
	}
	var ctx context.Context
	ctx, em.cancel = context.WithCancel(context.TODO())
	em.leaseID = leaseResp.ID
	_, err = em.lease.KeepAlive(ctx, em.leaseID)
	return err
}
func (em *ETCDMutex) lock() error {
	err := em.init()
	if err != nil {
		return err
	}
	// CreateRevision ==0 key
	//LOCK:
	txnResp, err := em.txn.If(clientv3.Compare(clientv3.CreateRevision(em.Key), "=", 0)).
		Then(clientv3.OpPut(em.Key, "", clientv3.WithLease(em.leaseID))).Commit()
	if err != nil {
		return err
	}
	if !txnResp.Succeeded { //txn.if
		return fmt.Errorf("")
	}
	return nil
}

func (em *ETCDMutex) UnLock() {
	em.cancel()
	em.lease.Revoke(context.TODO(), em.leaseID)
	fmt.Println("")
}

func main() {
	var conf = clientv3.Config{
		Endpoints:   []string{"172.16.196.129:2380", "192.168.50.250:2380"},
		DialTimeout: 5 * time.Second,
	}
	eMutex1 := &ETCDMutex{
		Conf: conf,
		Ttl:  10,
		Key:  "lock",
	}
	eMutex2 := &ETCDMutex{
		Conf: conf,
		Ttl:  10,
		Key:  "lock",
	}
	//groutine1
	go func() {
		err := eMutex1.lock()
		if err != nil {
			fmt.Println("groutine1")
			fmt.Println(err)
			return
		}
		//
		fmt.Println("groutine1")
		time.Sleep(10 * time.Second)
		defer eMutex1.UnLock()
	}()

	//groutine2
	go func() {
		err := eMutex2.lock()
		if err != nil {
			fmt.Println("groutine2")
			fmt.Println(err)
			return
		}
		//
		fmt.Println("groutine2")
		defer eMutex2.UnLock()
	}()
	time.Sleep(30 * time.Second)
}

